import react, { useContext } from "react"
import { DataContext } from "../../context"
import useFetch from "hooks/useFetch"






const MealsFromSearch = () => {
          const inputSearch = useContext(DataContext)


          const [data] = useFetch("search.php?s=", inputSearch.input)


          console.log("SEARCHDATA", data.fetchData)


          return (

                    <div className="meal">

                              {data && (
                                        data.fetchData.map(meal => (
                                                  <div>
                                                            <div className="meal_header">

                                                                      <img className="img_btn" data-index="1" src={meal.strMealThumb}></img>
                                                            </div>
                                                            <div className="meal_body">
                                                                      <h3>{meal.strMeal}</h3>

                                                            </div>
                                                  </div>
                                        ))
                              )}
                    </div>
          )



}

export default MealsFromSearch